const { Schema } = mongoose;

const Page = new mongoose.Schema(
  {
    name: {
      type: String,
      required: true,
      trim: true,
      maxlength: 15,
    },
    description: {
      type: String,
      maxlength: 50,
    },
    creationDate: {
      type: String,
      maxlength: 50,
    },
    websiteType: {
      type: String,
      maxlength: 50,
    },
    numberPages: {
      type: String,
      maxlength: 50,
    },
    industry: {
