    },
    slug: {
      type: String,
      required: true,
    },
    content: Object,
    user: {
      type: Schema.Types.ObjectId,
      ref: 'User',
      required: true,
    }
  },
  {
    timestamps: true,
  },
);

export default mongoose.model('Pages', Page);

